mapper.select:
select refresh_token
  from members
 where user_id = #{userId}

select user_id, user_password, user_name, email_id, email_domain, joined_time, user_role
  from members
 where user_id = #{userId}

mapper.update:
update members
   set refresh_token = #{refreshToken}
 where user_id = #{userId}

update members
   set refresh_token = null
 where user_id = #{userId}
